'use client';
import { Suspense, lazy } from "react";
import HeroCarousel from "@/components/ui/HeroCarousel";
import { useLanguage } from "@/contexts/LanguageContext";
import { useTranslation } from "../i18n/client";

// Lazy loading para componentes no críticos
const Experience = lazy(() => import("./Experience"));

export default function Home() {
  const { lng } = useLanguage();
  const { t } = useTranslation(lng);

  const carouselImages = [
    {
      src: "/photos/horizontal/personal_web_page-01.jpg",
      blurDataURL: "data:image/jpeg;base64,",
    },
    {
      src: "/photos/horizontal/personal_web_page-02.jpg",
      blurDataURL: "data:image/jpeg;base64,",
    },
    {
      src: "/photos/horizontal/personal_web_page-03.jpg",
      blurDataURL: "data:image/jpeg;base64,",
    },
    {
      src: "/photos/horizontal/personal_web_page-07.jpg",
      blurDataURL: "data:image/jpeg;base64,",
    },
  ];
  
  const carouselButton = t("carousel_button_1");

  return (
    <main className="flex min-h-screen flex-col items-center justify-between w-full overflow-x-hidden">
      {/* Sección crítica - carga inmediatamente */}
      <section className="w-full relative overflow-hidden">
        <HeroCarousel
          images={carouselImages}
          titles={Array(carouselImages.length).fill(t("name"))}
          descriptions={Array(carouselImages.length).fill(t("bio"))}
          buttonText={carouselButton}
          buttonTexts={Array(carouselImages.length).fill(carouselButton)}
          autoPlayInterval={6000}
        />
      </section>
      
      {/* Sección no crítica - lazy loading */}
      <Suspense 
        fallback={
          <div className="w-full py-16">
            <div className="max-w-4xl mx-auto h-96 bg-gray-200 dark:bg-gray-700 animate-pulse rounded-lg" />
          </div>
        }
      >
        <Experience />
      </Suspense>
    </main>
  );
}

